#define S(a, b, t) smoothstep( a, b, t )  

float DistLine( vec2 p , vec2 a , vec2 b ) {

    // Line ba에 Pa를 직교 투영한다.

    vec2 pa = p - a;
    vec2 ba = b - a;

    float t = clamp( dot( pa , ba ) / dot ( ba , ba ) , 0.0 , 1.0 );

    return length( pa - ba * t );

}

float N21( vec2 p ) {

    p = fract( p * vec2( 233.34 , 851.73));
    p += dot( p, p+23.45);
    return fract(p.x * p.y );
}

vec2 N22( vec2 p ) {
	float n = N21( p );
    return vec2( n , N21( p + n ) );
}



vec2 GetPos( vec2 id , vec2 offset ) {	// from id

    //vec2 n = N22( id + offset ) * iTime ;
    vec2 n = N22( id + offset ) * iTime ;

    //float x = sin ( iTime * n.x );
    //float y = cos ( iTime * n.y );

    return offset + sin( vec2(n) ) * 0.4;		// 0.4, it is Important that balls is in grid cell.

}

float Line( vec2 p , vec2 a , vec2 b ) {
	float d = DistLine( p , a, b );			// distance (a - b ) from p.
   	float m = S( 0.03, 0.01 , d );

    // 이게 오묘한 식이다, p와의 거리가 아니라, ( a - b )의 거리를 말한다.
    float d2 = length( a - b );
    m *= S( 1.2, 0.8 , d2 ) * 0.5 + S( 0.05, 0.03, abs( d2 -0.75 ) );

    return m;
}


float  Layer( vec2 uv ) {
	float m = 0.0;
    vec2 gv = fract(uv) - 0.5f;	// center point to ( 0 , 0 ) local uv
    vec2 id = floor(uv);


    //vec2 p = N22( id ) - 0.5;	// Random point based on each grid center.

    //vec2 p = GetPos( id );
    //float d = length( p - gv );


    int i = 0;		// index
    vec2 p[9];		// 9 cell

    for (float y = -1.0 ; y <= 1.0 ; y++ ) {
        for ( float x = -1.0 ; x <= 1.0 ; x++ ) {
        	p[i++] = GetPos( id , vec2( x , y ));
        }
    }


    float t = iTime * 10.0;
    for ( int i = 0 ; i < 9 ; i++ ) {
    	m += Line( gv, p[4] , p[i] );	// p[4] is mine

        vec2 j = ( p[i] - gv ) * 20.0;
        float sparkle = 1.0 / dot(j,j);



        m += sparkle * ( sin(t + fract(p[i].x ) * 10.0 ) * 0.5 + 0.5 ) ;
    }

    m += Line( gv, p[1] , p[3] );	// p[4] is mine
    m += Line( gv, p[1] , p[5] );	// p[4] is mine
    m += Line( gv, p[7] , p[3] );	// p[4] is mine
    m += Line( gv, p[7] , p[5] );	// p[4] is mine


    return m;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) /iResolution.y;
    vec2 mouse = iMouse.xy / iResolution.xy - 0.5;

    //float d = DistLine( uv , vec2(0) , vec2(1) );
    //float m = S( 0.1, 0.05 , d );

    //m = N22( uv ).x;

    float m = 0.0;
    float t = iTime * 0.1;

	// rotation
    float s = sin(t);
    float c = cos(t);
    mat2 rot = mat2( c , -s , s , c );
    uv *= rot;

    mouse *= 5.0;
    mouse *= rot;

    for ( float i = 0.0 ; i <= 1.0 ; i += 1.0/4.0) {
        float z = fract(i + t );
        float size = mix( 10.0 , 0.5 , z );

        float fadeIn = S( 0.0 , 0.5 , z );
        float fadeOut = S( 1.0 , 0.8 , z );
        float fade = fadeIn * fadeOut;			// 이것도 오묘한 식이다. fade In과 Fade Out을 어떻게 적용하는지 잘 볼 것.

    	m += Layer( uv * size +i * 20.0 - mouse ) * fade;
    }


	vec3 baseColor = sin( t * 5.0 * vec3( 0.345, 0.456 , 0.657) ) * 0.4 + 0.6 ; // Important technic.
    vec3 col = m * baseColor;

    //col.rg = gv;
    //if ( gv.x > 0.49 || gv.y > 0.49) col = vec3(1.0 , 0.0 , 0.0 );
    fragColor = vec4(col,1.0);
}

