#define BISECT_MIN 0.0
#define BISECT_MAX x
#define BISECTIONS int(iTime) % 10
#define EPSILON 0.001

#define draw(d, c) color = mix(color, c, smoothstep(unit, 0.0, d))

float test(in float tx, in float x) {
    return tx * tx - x;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    vec3 color = vec3(0.0);

    float x = 3.0;

    // Bisection loop
    float tmin = BISECT_MIN, tmax = BISECT_MAX, t;
    for (int bisection=0; bisection < BISECTIONS; bisection++) {
        t = (tmin + tmax) * 0.5;
        float e = test(t, x);
        if (e >= 0.0) tmax = t;
        if (e <  0.0) tmin = t;
        if (abs(e) < EPSILON) break;
    }

    // Draw bisected area
    draw(abs(uv.y - (tmin + tmax) * 0.5) - abs(tmax - tmin) * 0.5, vec3(0.0, 0.0, 0.5));

    // Draw the solution
    draw(abs(uv.y - sqrt(x)), vec3(0.0, 1.0, 0.0));

    // Draw bisection limits
    draw(abs(uv.y - tmin), vec3(1.0, 0.0, 0.0));
    draw(abs(uv.y - tmax), vec3(1.0, 0.0, 0.0));

    fragColor = vec4(color, 1.0);
}
