vec3 drawTree(vec2 uvTree,vec2 point,vec3 col){
	vec2 q=uvTree-point;
    float p=length(q);

    //画半径 atan返回q.y/q.x的arctan值域-pi到pi
    //反切值乘10是为了cos值增加更多循环（-1，1）每多一个循环就会多一个花瓣
    //cos参数增加 导致图像向右平移
    float r=0.05+0.03*cos(atan(q.y,q.x)*10.0+q.x*50.0-2.);

    //根据半径画圆
    col*=smoothstep(r,r+0.01,p);

    float d=0.005;
    float h=0.5;
    //得出一条竖线*只要下面部分变黑
    //col*=smoothstep(d,d+0.001,abs(q.x))*smoothstep(0.0,0.1,q.y);
    //和上面做法类似
   	d+=0.001*cos(q.y*100.0);
    //指数型 当参数小于0 值永远小于1呈曲线无线接近1
    d+=exp(-70.0*uvTree.y);
    //
    col*=1.0-(1.0-smoothstep(d,d+0.001,abs(q.x+0.05*sin(q.y*5.0))))*(1.0-smoothstep(0.0,0.01,q.y));
    return col;
}

vec3 drawsea(vec2 uvMan,vec3 col){

    //高度cos偏移
    float h=.05+0.02*cos(uvMan.x*20.0+iTime);

    col=mix(vec3(.3,.8,.9),col,smoothstep(h,h+.01,uvMan.y));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvTree=uv;

	//使uv的y值保持屏幕的比例 用来画出一个正圆
    uv.y*=iResolution.y/iResolution.x;
    vec2 uvMan=uv;
    vec3 col=vec3(1.0,0.65,0);
    //定义太阳半径和光晕
    float over=0.02/length(uv-vec2(0.9,0.48));
    //光晕散开
    float dist=pow(over,0.3);
    col*=dist;
    col=drawsea(uvMan,col);

    vec2 point1=vec2(0.85,0.4);
    vec2 point2=vec2(0.75,0.35);
	col=drawTree(uvTree,point1,col);
    col=drawTree(uvTree,point2,col);


    fragColor=vec4(col,1.0);
}
