#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.01
#define pi 3.141

float GetDist(vec3 p) {
    float v = simplex3d(p.xyz * 0.1);
    v *= simplex3d(p.xyz * 0.1 * 0.48956) * 2.0;

    float l = length(p - vec3(iTime * 5.0));

    v *= smoothstep(0.0, 10.0, l) * 10.0; //Avoid clipping inside the "solid's"
    v *= smoothstep(100.0, 0.0, l);

    float pd = smoothstep(1.0, 0.0, v);

    return pd;
}

float RayMarch(vec3 ro, vec3 rd) {
    float dO = 0.0;
    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-(0.5*iResolution.xy))/iResolution.y;
    vec4 col = vec4(0.0);

    float t = iTime;

    //Raymarching camera variables

    vec3 ro = vec3(t*5.0);
    vec3 rd = normalize(vec3(uv.xy, 1.0));

    //Raymarching direction rotation

    rd.zy *= rot(sin(t*pi*0.25)*pi*0.01); //X axis rotation
    rd.xy *= rot(pi*sin(t*pi*0.125)*0.01); //Z axis rotation
    rd.zx *= rot((t*pi*0.0625)+pi); //Y axis rotation

    //Raymarch processing

    float d = RayMarch(ro, rd);

    //Raymarch hit-point

    vec3 p = ro + rd * d;

    //Diffuse lighting, (Just ambience lighting)

    vec4 dif = vec4(0.0, 0.05, 0.087, 0.0);

    //Modifying color variable

    col = dif;
    col = mix(col, vec4(1.0, 1.0, 1.0, 0.0), smoothstep(0.0, MAX_DIST, d)); //Add a little lovely fog effect in the background

    fragColor = col;
}

mat2 rot(float r) {
    float s = sin(r);
    float c = cos(r);
    return mat2(c, -s, s, c);
}

