#define pi 3.141

float absolute_difference(vec2 uv, vec2 tr, vec2 to)
{
	float sum = 0.;
    float count = 0.;
    for (float x = -tr.x; x < tr.x; x += 1. / iResolution.x) {
        for (float y = -tr.y; y < tr.y; y += 1. / iResolution.y) {
            vec2 o = vec2(x, y);
            float c0 = texture(iChannel0, uv + o).r;
            float c1 = texture(iChannel0, to + o).r;
       		sum += abs(c0 - c1);
            ++count;
        }
    }
    return sum / count;
}

float maximum_difference(vec2 uv, vec2 tr, vec2 to)
{
	float m = 0.;
    for (float x = -tr.x; x < tr.x; x += 1. / iResolution.x) {
        for (float y = -tr.y; y < tr.y; y += 1. / iResolution.y) {
            vec2 o = vec2(x, y);
            float c0 = texture(iChannel0, uv + o).r;
            float c1 = texture(iChannel0, to + o).r;
       		m = max(m, abs(c0 - c1));
        }
    }
    return m;
}

float euclidean_difference(vec2 uv, vec2 tr, vec2 to)
{
	float sum = 0.;
    float count = 0.;
    for (float x = -tr.x; x < tr.x; x += 1. / iResolution.x) {
        for (float y = -tr.y; y < tr.y; y += 1. / iResolution.y) {
            vec2 o = vec2(x, y);
            float c0 = texture(iChannel0, uv + o).r;
            float c1 = texture(iChannel0, to + o).r;
       		sum += abs(c0 - c1 * c1);
            ++count;
        }
    }
    return pow(sum, .5) / count;
}

float rect(vec2 uv)
{
	uv = abs(uv);
    return max(uv.x, uv.y);
}

float line(vec2 uv, float a)
{
	vec2 d = vec2(cos(a), sin(a));
	float l = dot(uv, d);
	return abs(l);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy - .5;

    vec2 tr = vec2(.02);
    //vec2 to = vec2(sin(iTime * .1) * .3);
    vec2 to = iMouse.xy / iResolution.xy - .5;

    vec3 col = vec3(0);

    float d = 0.;
    float a = atan(uv.x, uv.y);
    if (uv.x + .5 < 1. / 3.)
   		d = absolute_difference(uv, tr, to);
    else if (uv.x + .5 < 2. / 3.)
    	d = maximum_difference(uv, tr, to) * .3;
    else
    	d = euclidean_difference(uv, tr, to) * 5.;

    col.r += texture(iChannel0, uv).r;
    col.g += smoothstep(.1, 0., d);
    col.g += smoothstep(.005, 0., line(uv + vec2(1./6., 0), 0.));
    col.g += smoothstep(.005, 0., line(uv - vec2(1./6., 0), 0.));

    col.b += rect((uv - to) / tr) - 1. < 0. ? 1. : 0.;

    fragColor = vec4(col, 1.0);
}
