void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    /*uv -= 0.5;
    uv *= .8;
    uv += 0.5;*/
    vec3 col = texture(iChannel0,uv).ggg;
    //col *= 4.;
    //col.g += iTime;
    col = vec3(sin(iTime*0.37)*col.g,(sin((iTime+0.45)*0.3)*0.5+0.5)*col.g,(sin(col.g+iTime*0.12)*0.5+0.5)*col.g);

    //col.b += sin(col.g+0.5);
    //uv = fragCoord/iResolution.xy;
	col *= 5.;
    col -= 1.25;
    fragColor = vec4(col,1.0);
}

#define iTime ((iTime+2948.)*0.1)

//uniform sampler2D backbuffer;
#define T(x,y) texture(iChannel0, tv+vec2(x,y)).rgb

float circle(vec2 p, float s) {
    if (length(p) < s) {
        return 1.;
    }else {
        return 0.;
    }
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
	vec2 tv = fragCoord.xy/R.xy;
	float mx = max(R.x, R.y);
    vec2 px = vec2(1./R.x,1./R.y);
	vec2 uv = -1. + 2. * tv;

	vec2 fc = fragCoord.xy;
 	vec2 nuv = (fc-0.5*R.xy)/R.y;
 	vec3 col = vec3(0.);
 	vec2 gv = fract(tv*2.);
 	float steps = 32.;
    col.r = steps/1e11;
    //nuv += 59.7;
    float t= (iTime+100.)*0.1;//(pointers[0].y/resolution.y);
    for (float i=1.; i<steps; i++) {
        float it = mod((i*2.)-t,steps)-(sin(iTime*0.02)*0.5+0.5)*((steps-1.)/2.);
        vec2 p = fract(nuv*it*it);
        p = p-0.5;
        //col.r *= (length(uv*0.05)+0.5)+(circle(p,cos(iTime*50.)*1.5*(length(uv*sin(iTime*50.))))*(2./steps)*steps*0.5);
        //p = p-fract((nuv*20.2)*((i*sin(time*0.5)*0.1)+time*0.1));
        col.r *= (length(uv*0.05)+0.5)+(circle(p,0.47)*(2./steps)*steps*0.5);
        //col.r *= m;
    }
    col.r *= 1e6;
    col.r -= 0.01;




    vec3 bak = T(0.,0.);
    vec3 c = bak;

    tv -= 0.5;
    tv *= 0.999+((c.r+c.b+c.g)/3.)*0.0025;
    float tl = length(tv);

    tv += 0.5;
	vec3 u = T(0.,px.y);
    vec3 d = T(0.,-px.y);
    vec3 l = T(px.x,0.);
    vec3 r = T(-px.x,0.);

    float s = (u.g+d.g+l.g+r.g)/4.;
    s *= 10.;
    px += s;
	u = T(0.,px.y);
    d = T(0.,-px.y);
    l = T(px.x,0.);
    r = T(-px.x,0.);
    s *= 0.1;

    if (length(tv-0.5) < 0.0025) {
        col.g += 1.;
    }

    if (s*10. > 0.0001 && col.r < 1e-8) {
        col.g += s+0.05*(sin(iTime*0.85)*0.5+0.6)*(1.-tl)*1.;
    }
    if (col.g < 0.123) {
    	col.g *= 8.*s;
    }

	col += vec3(0.01);
	fragColor = vec4(col, 1.0);
}
