void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 col = vec4(0.0);

    float pixelation = 4.0; //Round up 4 of each pixel of the resolution

    fragCoord.xy = floor(fragCoord.xy/pixelation)*pixelation;
    vec2 uv = fragCoord.xy/iResolution.xy;

    col = texture(iChannel0, uv);

    float pIntensity = 1.0; //Posterize Intensity, or color range.
    col = (floor((col+0.5)*pIntensity)/pIntensity);

    fragColor = col;
}
