const float FLT_MAX = (1.0f - pow(2.0f, -24.0f)) * pow(2.0f, 128.0f);
float packvec2(in vec2 v) {
	uint ui = packUnorm2x16(v);
    return clamp(uintBitsToFloat(ui), -FLT_MAX, FLT_MAX); //clamp to avoid error on extreme values
}

vec2 unpackvec2(in float f) {
	uint ui = floatBitsToUint(f);
	return unpackUnorm2x16(ui);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec4 data = texelFetch(iChannel0, ivec2(fragCoord), 0);
    float u = packvec2(data.rg);
    float v = packvec2(data.ba);

    fragColor.rg = unpackvec2(u);
    fragColor.ba = unpackvec2(v);    
}
