void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float t = (1.1 + sin(iTime * 0.5)) * 2.0;
    vec2 cellSize = vec2(t / 16.0, t / 9.0);
    //Cell coords:
    vec2 cell = vec2( int(uv.x / cellSize.x), int(uv.y / cellSize.y) );

    bool isWhite = false;

    if(int(cell.x) % 2 == 0)
        isWhite = true;

    if(int(cell.y) % 2 == 0)
        isWhite = !isWhite;

    if(isWhite)
        fragColor = vec4(0.9, 0.9, 1, 1);
    else
        fragColor = vec4(0.1, 0.1, 0, 1);
}
