#define freq(f) texture(iChannel0, vec2(f, 0.15)).x * 0.9

float avgFreq(float start, float end, float step) {
    float div = 0.0;
    float total = 0.0;
    for (float pos = start; pos < end; pos += step) {
        div += 1.0;
        total += freq(pos);
    }
    return total / div;
}    


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float bassFreq = pow(avgFreq(0.0, 0.1, 0.01), 0.85);
    float medFreq = pow(avgFreq(0.1, 0.6, 0.01), 0.85);
    float topFreq = pow(avgFreq(0.6, 1.0, 0.01), 0.95);

    vec2 uv = fragCoord/iResolution.xy;
    vec2 q = uv - vec2 (0.5,0.5);
    vec2 a = uv + mod(0.5,0.5);     
    vec3 col = vec3 ( 0.2, .35, .5 );
    float r = 0.02 + 0.9 * sin(atan(q.y,q.x)* iTime * 5.0 * sin(bassFreq /= 6.3) ); 
    float d = 0.09 * 7.0 * fract(atan(a.y,a.x) * iChannelTime[0] * fract(a.x *= medFreq *.4) );

    col *= mix(r, r*0.1, length( q / d*2.23 * fract(topFreq * 2.3)) ); 
    fragColor = vec4(col,1.0);
}
