//I hope this helps new users start programming and learning math easily ...
//Happy New Year 2021

//define background colors 
#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

float add_Plane( vec3 p )
{
	return p.y;
}
// message 2021
float message(vec2 uv) {   
    // to alter in the icon
    uv-=vec2(0.,1.); 
    if ((uv.x<0.)||(uv.x>=64.)||(uv.y<0.)||(uv.y>=16.)) return -1.; 
    int i=1, bit=int(pow(2.,floor(32.-uv.x)));

    if (int(uv.y)==0) i= 0/bit; // 
    if (int(uv.y)==1) i= -1010580484/bit; // 
    if (int(uv.y)==2) i= -1111638532/bit; // 
    if (int(uv.y)==3) i= -1078083588/bit; //    
    if (int(uv.y)==4) i= -1078083588/bit; // 
    if (int(uv.y)==5) i= -1078083588/bit; //
    if (int(uv.y)==6) i= -541204484/bit; //
    if (int(uv.y)==7) i= -272764932/bit; //
    if (int(uv.y)==8) i= -138545156/bit; //
    if (int(uv.y)==9) i= -71435268/bit; //
    if (int(uv.y)==10)i= -37880324/bit; //
    if (int(uv.y)==11)i= -1111638564/bit; //
    if (int(uv.y)==12)i= -1111638548/bit; //
    if (int(uv.y)==13)i= -1111638540/bit; //
    if (int(uv.y)==14)i= -1010580484/bit; //
    if (int(uv.y)==15)i= 0/bit; //

 	return float(i-2*(i/2));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    if (iResolution.y<300.) 
    {
    float c=message(fragCoord.xy/16.);
        if(c>=0.){fragColor=vec4(c);return;
    }
    }

    // define a vector type vec3
    vec3 col = vec3(1.0);
    // show white color 
    fragColor = vec4(col,1.0);
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = ((fragCoord)-iResolution.xy)/iResolution.y+1.0;
    col =  normalize( vec3(p,cos(iTime)));
    float add_plane = add_Plane(col);
    // Time varying pixel color
    vec4 f = texelFetch(iChannel0, ivec2(0.5),0);
    vec3 col1 = normalize(vec3(col+sin(iTime-f.x)/add_plane*sin(f.x)));
    vec3 col2 = normalize(vec3(tan(col1/f.x)/(sin(f.x*col)/add_plane-f.x)));
    vec3 col3 = normalize(vec3(f.x*(cos(f.x+col1)*tan(col+f.x/col))));
    // Output to screen
    fragColor = vec4(col3,0.0);
}
