#define PI 3.14

// 	Colors
#define RED 	vec3(1.00, 0.00, 0.00)
#define BLUE 	vec3(0.04, 0.00, 0.38)
#define BEIGE 	vec3(0.55, 0.35, 0.35)
#define VIOLET 	vec3(0.24, 0.12, 0.26)


#define SPEED 16.0


//	Usual rotation.
mat2 rotate(float angle)
{
	return mat2(cos(angle), -sin(angle),
                sin(angle), cos(angle));
}


//	Convert time to [0; 4 * PI].
float normalizeTime()
{
	return iTime - float(int(iTime / 4.0 / PI)) * 4.0 * PI;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.xy;

    float time = normalizeTime();

    float angle = abs(acos(dot(rotate(-iTime) *  rotate(length(uv) * 32.0) * vec2(1, 0), uv) / length(uv)));

    vec2 p = length(uv) * (rotate(angle) * uv);

    float pLength = length(p);

    vec3 color = vec3(pLength);

    float timePercent = time / (4.0 * PI);

    if (angle < 1.5 * abs(0.4 * sin(iTime) + 0.8))
    {
        color = RED;

        if (SPEED * pLength < time)
        {
           color = mix(BEIGE, color, timePercent); 
        }
    }
    else
    {
        color = VIOLET;

        if (SPEED * pLength < time)
        {
            color = mix(BLUE, color, timePercent);
        }
    }

    color *= textureLod(iChannel0, p, 0.0).xyz;

    //	Gamma correction.
    color = pow(color, vec3(0.4545 * 2.0));


    fragColor = vec4(color,1.0);
}
